import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../sidebar/index";

type HeaderProps = {
  userName: string;
  userImage?: string;
};

function Header({ userName, userImage }: HeaderProps) {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const navigate = useNavigate();

  // Foto padrão caso o usuário não tenha nenhuma
  const defaultUserImage =
    "data:image/png;base64,";
  const menuIcon =
    "data:image/svg+xml;base64,";

  return (
    <>
      <header className="bg-dark position-relative">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-3 w-100 position-relative">
          {/* Botão para abrir/fechar o menu lateral */}
          <button
            className="btn btn-dark"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            <img src={menuIcon} alt="Menu" width={32} height={32} />
          </button>

          {/* Título central */}
          <div
            className="position-absolute top-50 start-50 translate-middle text-white fw-bold"
          >
            GERENCIAMENTO DE MARKETING
          </div>

          {/* Menu do perfil do usuário */}
          <div className="ms-auto dropdown">
            <a
              className="d-flex align-items-center text-white text-decoration-none dropdown-toggle"
              href="#"
              id="userMenu"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              <img
                src={userImage || defaultUserImage}
                alt="Foto do usuário"
                className="rounded-circle"
                width={40}
                height={40}
              />
            </a>

            <ul
              className="dropdown-menu dropdown-menu-end"
              aria-labelledby="userMenu"
            >
              <li>
                <span className="dropdown-item-text fw-bold">{userName}</span>
              </li>
              <li>
                <button
                  className="dropdown-item"
                  onClick={() => navigate("/editar-conta")}
                >
                  Editar perfil
                </button>
              </li>
              <li>
                <hr className="dropdown-divider" />
              </li>
              <li>
                <a className="dropdown-item text-danger" href="/logout">
                  Sair
                </a>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      {/* Sidebar */}
      <Sidebar isOpen={sidebarOpen} closeSidebar={() => setSidebarOpen(false)} />
    </>
  );
}

export default Header;
